import React from "react";
import { Modal,Image } from "antd";
import {ICONS} from './IconsChooser';
import { FundPool } from "../../state";
import * as web3 from '@solana/web3.js';
import {InvestorForm} from './InvestorForm';
import {ShareView} from './ShareView';

interface InvestmentFormProps  {

    modalPresented : boolean,

    setModalPresented : (presented : boolean) => void ,

    submitInvestor : () => void,

    selectedFundPool : FundPool | undefined ,

    investorLoading : boolean,

    setValuesOf : (token_count : number, amount : number, errorMessage : string | null  ) => void, 
}


export const InvestmentModalForm : React.FC <InvestmentFormProps> = ({modalPresented, setModalPresented,
    submitInvestor, selectedFundPool, investorLoading, setValuesOf}) =>{

    return <Modal destroyOnClose={true} 
    title={
    <div>
      <Image width={30} title="Icon" alt="Icon" style={{ verticalAlign: 'middle', 
      marginTop:"5px", marginRight:"20px"}}
        height={30} preview={false}
        src={(selectedFundPool?.icon ?? 0) >= 0 ? ICONS[selectedFundPool?.icon ?? 0] : "none"}
        fallback="data:image/png;base64,"/>
      
    <label style={{ color: "white" , marginLeft:"10px"}}>{ 
    (selectedFundPool?.address?.toBase58() ?? "") }</label>
    </div>
    }
        className="roundModal"
         visible={modalPresented}
          onCancel={()=>{

              setModalPresented(false);
            
          }}

          onOk={()=>{

             submitInvestor();

          }}

          okText="Sign &amp; Invest"

          okButtonProps={{ disabled: false }}
          cancelButtonProps={{ disabled: false }}>
         <InvestorForm setValuesOf={setValuesOf} 
         tokenToSol={selectedFundPool?.token_to_sol_ratio ?? 0} 
         remainingToken={selectedFundPool?.rm_token_count ?? 0}
         commission={(selectedFundPool?.fee_in_lamports ?? 0) / web3.LAMPORTS_PER_SOL}
         loading={investorLoading}
         />
        
    </Modal>

    
}

interface ShareModalFormProps {

    address : string,

    modalPresented : boolean,

    setModalPresented : (presented : boolean) => void, 
}

export  const ShareModalForm : React.FC <ShareModalFormProps> = ({ address, modalPresented, 
    setModalPresented})=>{

    return <Modal 
    title={<label style={{ color: "white" }}>{ "Share " + address}</label>}
        className="shareViewModal"
         visible={modalPresented}
          onCancel={()=>{

              setModalPresented(false);
            
          }}

          okText="OK"

          okButtonProps={{ disabled: true  }}
          cancelButtonProps={{ disabled: false }}>
          <ShareView address={"fundpool/"+ address} 
          quote="Solfund Fund Pool" hashtag="#Solfund #solana #blockchain #mutual fund"
          />
    </Modal>

}