import React, {useState, useEffect} from 'react';
import useFundPool from '../../Hooks/useFundPool';
import {FundPool} from '../../state';
import { error } from '../../utils/Mesg';
import * as web3 from '@solana/web3.js';
import {Image, Card} from 'antd';
import {ICONS} from './IconsChooser';

interface FundPoolCardViewProps {

    manager : string,
    
    address : string,

    tokenCount : number, 

    lamports : number, 

    icon : number,

    className : string, 

}

export const FundPoolCardView : React.FC <FundPoolCardViewProps> = ({address, manager, 
    tokenCount, lamports, icon, className}) => {

    return <div className={className}>
    
    <Card style={{maxWidth:"70%",margin:"auto"}} cover={
        <Image width={50} title="Icon" alt="Icon"
        height={50} preview={false} style={{marginTop:"5px"}}
        src={(icon ?? 0) >= 0 ? ICONS[icon ?? 0] : "none"}
        fallback="data:image/png;base64,"
        />}>
        <Card type="inner" title="Address">
        <span style={{maxWidth:"200px",textOverflow:"ellipsis"}}>{address}</span>
        </Card>        
       
        <Card type="inner" title="Manager">
        <span style={{maxWidth:"200px",textOverflow:"ellipsis"}}>{manager}</span>
        </Card>        
       
        <Card type="inner" title="Number Of Tokens">
        {tokenCount}
        </Card>
        <Card type="inner" title="Amount In SOL">
        {(lamports / web3.LAMPORTS_PER_SOL).toFixed(9)}
        </Card>

    </Card>
    </div>;

}