import React, {useState, useEffect} from 'react';
import {Image} from 'antd';
import {ICONS} from './IconsChooser';
import './css/FundPoolCardView.css';
import {format_pub_key_shorter} from '../../state/';
import {Tooltip,Button} from 'antd';
import {InfoOutlined} from '@ant-design/icons';
import {Link} from 'wouter';
import * as web3 from '@solana/web3.js';
import useInvestor from '../../Hooks/useInvestor';
import useFundPool from '../../Hooks/useFundPool';
import { FundPoolInvestor, FundPool } from '../../state/';


export interface InvestorFundCardViewProps {

    
    address : web3.PublicKey,

    className : string, 

}

export const InvestorFundCardView : React.FC <InvestorFundCardViewProps> = 
({  address, className}) => {

    const[,,,readInvestor] = useInvestor();

    const[,,readFundPool] = useFundPool();

    const [tokenCount, setTokenCount] = useState(0);
    
    const [poolTokenCount, setPoolTokenCount] = useState(0);

    const [poolAddress, setPoolAddress] = useState("...");

    const [poolManager, setPoolManager] = useState("...");

    const [icon, setIcon] = useState(0);
    

    async function readData(pubkey : web3.PublicKey){

        readInvestor(pubkey.toBase58(), (res : FundPoolInvestor | Error) =>{

            if ( !(res instanceof Error)){
                //tokenCount, poolTokenCount, poolAddress, poolManager,  icon, className
               
                readFundPool(res.pool_address.toBase58(), (fp : FundPool |Error)=>{

                    if ( !(fp instanceof Error)){


                        setTokenCount(res.token_count);
                        setPoolTokenCount( fp.token_count);
                        setPoolAddress (fp.address.toBase58());
                        setPoolManager ( fp.manager.toBase58());
                        setIcon(fp.icon);

                    }

                    
                })
            }
            else {

                console.log("err.x", res);
            }
        })
       
    }


    useEffect(() => {

        readData(address);
       
    }, [address]);

    return <div className={className}>
    
        <div>
        <Link href={"/fundpool/"+poolAddress}>
        <Button shape="circle" style={{float:"right"}}>
        <InfoOutlined />
        </Button>
        </Link>
        <span>
        <Image width={50} title="Icon" alt="Icon" style={{ verticalAlign: 'middle' }}
        height={50} preview={false}
        src={(icon ?? 0) >= 0 ? ICONS[icon ?? 0] : "none"}
        fallback="data:image/png;base64,"/>
        </span>
        <Tooltip overlayStyle={{maxWidth: '500px'}} placement="top" title={poolAddress} arrowPointAtCenter>
        <span style={{fontWeight: "bolder",margin:"10px 10px 50px 10px",height:"40px",padding:"5px 5px 30px 5px"}}>
        {format_pub_key_shorter(poolAddress)}
        </span>
        </Tooltip>
        </div>
        <Tooltip placement="bottom" overlayStyle={{maxWidth: '500px'}} title={poolManager} arrowPointAtCenter>
        {format_pub_key_shorter(poolManager)}
        </Tooltip>

        <div className="item">Token : {poolTokenCount}</div>

        <div className="item">Your Share : {tokenCount} Tokens ({((tokenCount/poolTokenCount) * 100).toFixed(2)}%)</div>
     
    </div>;

}